<template>    <div class="abc-main">        <el-row>            <el-col :span="12" :offset='4' class="el-input" v-if="!multiple">                <input  v-model.trim="search.s" width="100"  class="el-input__inner" id="single-input"                        placeholder="订单号 /申请号 /号码 /servid /或者 一大波内容"/>            </el-col>            <el-col v-else class="el-input" :span="12" :offset='4' >                 <textarea class="el-input__inner" id="multiple-input"                           type="textarea"                           minRows=3                           maxRows=4 style="min-height:80px"                           placeholder="请输入内容"                           v-model.trim="search.s">                </textarea>            </el-col>            <el-col :span="4" class=""  >                <el-button type="primary" @click="filtercos({s:search.s,m:multiple})">查询</el-button>            </el-col>        </el-row >        <el-row>            <co-nbr :multiple="multiple">            </co-nbr>        </el-row>    </div></template><script lang=babel>  import Vue from  'vue';  import { mapActions,mapGetters,mapState } from 'vuex';  import {interacts} from '../../vuex/index' //引入vuex中的store  import co_nbr from './co_nbr.vue';  export default{    data(){      return {        search: {          s: '1511222014366501010',          m: this.multiple        }      }    },    components:{      'co-nbr':co_nbr    },    methods: {      ...mapActions({        'filtercos':interacts.abc.act_filtercos // 映射 this.add() 为 this.$store.dispatch('increment')      })    },    computed:{      multiple() {        let self = this,s = this.search.s;        if (!s) return;        const r = (/^[-a-z0-9@]+$/i).test(s);        let m = !r && s.length > 12;        let selector = '#'+(!m?'single-input':'multiple-input');        Vue.nextTick(()=>document.querySelector(selector).focus());        return m;      },    },    mounted(){      let s = this.$route.query.s;      if(!!s){        this.$store.dispatch(interacts.abc.act_filtercos,this.search)      }    }  }</script><style scoped>    .abc-main {        background-color: #fff;    }    .el-icon-arrow-right{        float:right;    }</style>