<template>    <div>    <template type="text/x-template"  v-for="item in model" v-if="!item.hidden">        <el-submenu :index="item.path" >            <template slot="title"><i :class="item.iconCls"></i>{{item.name}}</template>            <el-menu-item v-for="child in item.children" :index="child.path" v-if="!!item.children">                <my-sub-menu :model="child"></my-sub-menu>            </el-menu-item>        </el-submenu>    </template>    </div></template><script lang="babel">    export default {        name:'MySubMenu',        props: ['model']    }</script>