<template>    <div class="abc-main">        <el-row>            <el-col :span="12" :offset='4' class="el-input" v-if="!search.multi_s">                <input  v-model.trim="search.s" @keyup="intel" width="100"  class="el-input__inner"                        placeholder="订单号 /申请号 /号码 /servid /或者 一大波内容" ref="singleInput"/>            </el-col>            <el-col v-else class="el-input" :span="12" :offset='4' >                 <textarea @keyup="intel"  class="el-input__inner"                           type="textarea"                           minRows=3                           maxRows=4 style="min-height:80px"                           placeholder="请输入内容"                           v-model.trim="search.s" ref="multiInput">                </textarea>            </el-col>            <el-col :span="4" class=""  >                <el-button type="primary" @click="filtercos(search.s)">查询</el-button>            </el-col>        </el-row >        <el-row>            <co-nbr>            </co-nbr>        </el-row>    </div></template><script lang=babel>  import Vue from  'vue';  import { mapActions } from 'vuex';  import {muteTypes} from '../../vuex/index' //引入vuex中的store  import co_nbr from './co_nbr.vue';  export default{    data(){      return {        search: {          s: '', multi_s: false        }      }    },    components:{      'co-nbr':co_nbr    },    methods: {      intel() {        let self = this;        let s = this.search.s;        if (!s) return;        const r = (/^[-a-z0-9@]+$/i).test(s);        if (this.search.multi_s = !r && s.length > 12)          Vue.nextTick(() => self.$refs.multiInput.focus(),0);        else          Vue.nextTick(() => self.$refs.singleInput.focus(),0);      },      ...mapActions({        'filtercos':muteTypes.abc.filtercos // 映射 this.add() 为 this.$store.dispatch('increment')      })    },    mounted(){      function openrun(scope) {        try {          var s = location.search().s.trim();          if (s)            scope.filtercos(s, true)        } catch (e) { }      }    }  }</script><style scoped>    .abc-main {        background-color: #fff;    }</style>